import React, { useState } from 'react'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import { Container } from '../styled/layoutStyle'
import firebase from '../config'
import { serverTimestamp } from 'firebase/firestore'

function Calculate() {

    const [Start, setStart] = useState(0)
    const [End, setEnd] = useState(0)
    const [PrimeArr, setPrimeArr] = useState([])
    const [cntprime, setcntprime] = useState(0)
    const firebaseRef = firebase.firestore().collection('History')

    function isPrime(num) {
        for (let i = 2; i * i <= num; i++)
            if (num % i === 0)
                return false;
        return num > 1;
    }

    const checkNumber = (e) => {
        e.preventDefault();

        if (Start >= End) {
            alert('End number should more than Start number')
        } else {
            for (let i = Start; i <= End; i++) {
                if (isPrime(i)) {
                    console.log(i + ' is Prime');
                    PrimeArr.push(i)
                    setcntprime(PrimeArr.length)
                }
            }firebaseRef.doc().set(dataObj);
        }
    }
    const dataObj = {
            start: `${Start}`,
            end: `${End}`,
            prime: `${PrimeArr}`,
            cntprime: `${cntprime}`,
            timestamp: serverTimestamp()
        }
        const renderHistory = () => {
            return (
              <Container >
                <table border='1'>
                  <tr>
                    <thead>
                      <th scope="col">Start</th>
                      <th scope="col">End</th>
                      <th scope="col">Prime count</th>
                      <th scope="col">Prime number</th>
                      <th>timestamp</th>
                    </thead>
                    <tbody>
                      {History.map((item, i) => (
                        <tr key={i}>
                          <td>{item.start}</td>
                          <td>{item.end}</td>
                          <td>{item.cntprime}</td>
                          <td>{item.prime}</td>
                          <td>{`${item.timestamp}`}</td>
                        </tr>
                      ))}
                    </tbody>
                  </tr>
                </table>
        
        
        
              </Container>
            );
          };
    return (
        <Container>
            <h1>Prime number Calculate</h1>
            <Form onSubmit={checkNumber}>
                <Form.Group controlId='Start'>
                    <Form.Label>Start</Form.Label>
                    <Form.Control type="number" min={10} max={999} onChange={e => setStart(e.target.value)} required />
                </Form.Group>
                <Form.Group controlId='End'>
                    <Form.Label>End</Form.Label>
                    <Form.Control type="number" min={11} max={1000} onChange={e => setEnd(e.target.value)} required />
                </Form.Group>
                <Button size="lg" block="block" type="submit">
                    Calculate
                </Button>
            </Form>
            <div>Prime number is : {' ' + PrimeArr}</div>
            <div>Prime count is : {cntprime}</div>
            <div>History</div>
        </Container>
    )
}

export default Calculate
